  --shadow-lg: 0px 10px 15px -3px hsl(340 40% 30% / 0.12), 0px 4px 8px -2px hsl(340 40% 30% / 0.10);
  --shadow-xl: 0px 20px 25px -5px hsl(340 40% 30% / 0.14), 0px 10px 12px -4px hsl(340 40% 30% / 0.10);
  --shadow-2xl: 0px 25px 50px -12px hsl(340 40% 30% / 0.20);
  --tracking-normal: 0em;
