                {/* Hero Image */}
                <div className="space-y-3">
                  <Label className="text-lg font-medium flex items-center gap-2">
                    🏞️ Hình Nền Trang Chủ
                  </Label>
                  <div className="space-y-3">
                    <div className="flex gap-2">
                      <Input
                        value={formData.heroImage}
