    bgColor: "bg-pink-50 dark:bg-pink-950/20"
  },
  {
    title: "Lịch Trình",
    url: "/admin/schedule",
    icon: Calendar,
    description: "Quản lý lịch trình sự kiện",
    color: "text-green-500",
    bgColor: "bg-green-50 dark:bg-green-950/20"
  },
  {
